import React, { useState } from "react";
import {
  ChakraProvider,
  Box,
  Flex,
  Button,
  Input,
  useColorMode,
  useColorModeValue,
  Text,
  VStack,
  HStack,
  Avatar,
  Spacer,
  IconButton,
} from "@chakra-ui/react";
import { MoonIcon, SunIcon } from "@chakra-ui/icons";

function Login({ onLogin }) {
  const [username, setUsername] = useState("");

  function handleLogin(e) {
    e.preventDefault();
    if (username.trim()) {
      onLogin(username.trim());
    }
  }

  return (
    <Flex minH="100vh" align="center" justify="center" bg={useColorModeValue("gray.100", "gray.800")}>
      <Box bg={useColorModeValue("white", "gray.700")} p={8} rounded="md" shadow="md" minW="320px">
        <Text fontWeight="bold" fontSize="2xl" mb={6} textAlign="center">
          New Chat Elite
        </Text>
        <form onSubmit={handleLogin}>
          <VStack spacing={4}>
            <Input
              placeholder="Enter a username"
              value={username}
              onChange={e => setUsername(e.target.value)}
              autoFocus
            />
            <Button type="submit" colorScheme="blue" width="full">
              Log In
            </Button>
          </VStack>
        </form>
      </Box>
    </Flex>
  );
}

function Sidebar() {
  // Placeholder for server/channel list
  return (
    <Box
      w="72px"
      bg={useColorModeValue("gray.200", "gray.900")}
      borderRightWidth="1px"
      h="100vh"
      p={2}
      display="flex"
      flexDir="column"
      alignItems="center"
      gap={2}
    >
      {/* Example avatars/groups */}
      <Avatar name="A" size="md" />
      <Avatar name="B" size="md" />
      <Avatar name="C" size="md" />
      <Spacer />
    </Box>
  );
}

function ChannelBar() {
  // Placeholder for channel navigation
  return (
    <Box
      w="240px"
      bg={useColorModeValue("gray.100", "gray.800")}
      borderRightWidth="1px"
      h="100vh"
      p={4}
    >
      <VStack align="start" spacing={4}>
        <Text fontWeight="bold"># general</Text>
        <Text color="gray.500" fontSize="sm"># random</Text>
      </VStack>
    </Box>
  );
}

function TopBar({ username }) {
  const { colorMode, toggleColorMode } = useColorMode();
  return (
    <Flex
      h="56px"
      align="center"
      px={4}
      borderBottomWidth="1px"
      bg={useColorModeValue("white", "gray.700")}
      justify="space-between"
    >
      <Text fontWeight="bold"># general</Text>
      <HStack spacing={2}>
        <Text color="gray.500" fontSize="sm">
          {username}
        </Text>
        <Avatar name={username} size="sm" />
        <IconButton
          icon={colorMode === "dark" ? <SunIcon /> : <MoonIcon />}
          onClick={toggleColorMode}
          size="sm"
          aria-label="Toggle color mode"
        />
      </HStack>
    </Flex>
  );
}

function ChatArea() {
  // Placeholder messages
  const messages = [
    { user: "Alice", text: "Hey!" },
    { user: "Bob", text: "Hello there." },
    { user: "You", text: "Hi everyone!" },
  ];
  return (
    <Flex direction="column" h="calc(100vh - 56px)" bg={useColorModeValue("gray.50", "gray.800")} p={6}>
      <VStack align="start" spacing={4} flex="1" overflowY="auto">
        {messages.map((msg, idx) => (
          <HStack key={idx} align="center">
            <Avatar name={msg.user} size="sm" />
            <Box>
              <Text fontWeight="bold" fontSize="sm">{msg.user}</Text>
              <Text>{msg.text}</Text>
            </Box>
          </HStack>
        ))}
      </VStack>
      <Box mt={4}>
        <Input placeholder="Type a message..." />
      </Box>
    </Flex>
  );
}

function MainLayout({ username }) {
  return (
    <Flex minH="100vh">
      <Sidebar />
      <ChannelBar />
      <Flex direction="column" flex="1">
        <TopBar username={username} />
        <ChatArea />
      </Flex>
    </Flex>
  );
}

export default function App() {
  const [user, setUser] = useState(null);

  return (
    <ChakraProvider>
      {user ? <MainLayout username={user} /> : <Login onLogin={setUser} />}
    </ChakraProvider>
  );
}
